void ui_event_IntroScreen(lv_event_t * e)
{
    lv_event_code_t event_code = lv_event_get_code(e);
    lv_obj_t * target = lv_event_get_target(e);
    if(event_code == LV_EVENT_SCREEN_LOAD_START) {
        _ui_opacity_set(ui_IS_introText_D1, 0);
        _ui_opacity_set(ui_IS_introText_N1, 0);
        _ui_opacity_set(ui_IS_introText_D2, 0);
        _ui_opacity_set(ui_IS_introText_E1, 0);
        _ui_opacity_set(ui_IS_introText_V1, 0);
        _ui_opacity_set(ui_IS_introText_I1, 0);
        _ui_opacity_set(ui_IS_introText_C1, 0);
        _ui_opacity_set(ui_IS_introText_E2, 0);
    }
    if(event_code == LV_EVENT_SCREEN_LOADED) {
        IntroDropIn_Animation(ui_IS_introText_D1, 400);
        IntroDropIn_Animation(ui_IS_introText_N1, 600);
        IntroDropIn_Animation(ui_IS_introText_D2, 800);
        IntroDropIn_Animation(ui_IS_introText_E1, 1000);
        IntroDropIn_Animation(ui_IS_introText_V1, 1200);
        IntroDropIn_Animation(ui_IS_introText_I1, 1400);
        IntroDropIn_Animation(ui_IS_introText_C1, 1600);
        IntroDropIn_Animation(ui_IS_introText_E2, 1800);
        IntroRevealTxt_Animation(ui_IS_touchScreenTxt, 2500);
        dndv_IS_updatePlayerData(e);
    }
}
